func TestDESEncrypt(t *testing.T) {

	list := []map[string]string{{
		"target": "ku1kMuU0fMY=",
		"source": "123456",
	}, {
		"target": "/z2JReJjups=",
		"source": "222222",
	}}

	for _, v := range list {
		if DESEncrypt(v["source"]) != v["target"] {
			fmt.Printf("item=%v\n", v)
		}
		if DESDecrypt(v["target"]) != v["source"] {
			fmt.Printf("item=%v\n", v)
		}
	}
}

